                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       package com.rapzinator.goodmusic.screen.login

import android.annotation.SuppressLint
import android.content.Intent
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.text.TextUtils
import android.view.View
import android.widget.Toast
import com.google.firebase.auth.FirebaseAuth
import com.rapzinator.goodmusic.databinding.ActivityLoginBinding
import com.rapzinator.goodmusic.screen.registration.RegistrationActivity
import com.rapzinator.goodmusic.screen.reset.ResetPasswordActivity
import com.rapzinator.goodmusic.screen.main.MainActivity

class LoginActivity : AppCompatActivity() {
    private lateinit var binding: ActivityLoginBinding
    private lateinit var auth: FirebaseAuth

    @SuppressLint("ShowToast")
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityLoginBinding.inflate(layoutInflater)
        setContentView(binding.root)

        auth = FirebaseAuth.getInstance()

        binding.btnLogin.setOnClickListener {

            val email: String = binding.emailLogin.text.toString().trim()
            val password: String = binding.passwordLogin.text.toString().trim()

            if (TextUtils.isEmpty(email)) {
                binding.emailLogin.error = "Required Field.."
            }

            if (TextUtils.isEmpty(password)) {
                binding.passwordLogin.error = "Required Field.."
                return@setOnClickListener
            }
            showProgressBar()

            auth.signInWithEmailAndPassword(email, password).addOnCompleteListener { task ->

                if (task.isSuccessful) {
                    startActivity(Intent(this, MainActivity::class.java))
                    Toast.makeText(this, "Successful", Toast.LENGTH_SHORT)
                } else {
                    Toast.makeText(this, task.exception?.message, Toast.LENGTH_SHORT)
                }
                hideProgressBar()
            }
        }

        binding.signUp.setOnClickListener {
            startActivity(Intent(this, RegistrationActivity::class.java))
        }

        binding.forgotPwd.setOnClickListener {
            startActivity(Intent(this, ResetPasswordActivity::class.java))
        }
    }

    private fun showProgressBar() {
        binding.progressBar.visibility = View.VISIBLE
    }

    private fun hideProgressBar() {
        binding.progressBar.visibility = View.GONE
    }
}